pair E = dir(45*4+x);
pair F = dir(45*5+x);
pair G = dir(45*6+x);
pair H = dir(45*7+x);
draw(A--B--C--D--E--F--G--H--cycle);
label("$A$", A, NE);
label("$B$", B, N);
label("$C$", C, N);
label("$D$", D, NW);
label("$E$", E, SW);
label("$F$", F, S);
label("$G$", G, S);
label("$H$", H, SE);
draw(A--D--G);
